function getElementsByClass(tag, clas) {
    var elements = document.getElementsByTagName(tag);
    var results = [];
    for (var i = 0; i < elements.length; i++) {
        if ($(elements[i]).hasClass(clas)) {
            results.push(elements[i]);
        }
    }
    return results;
}

